import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Overview from './components/Overview';
import Storage from './components/Storage';
import Reports from './components/Reports';
import Settings from './components/Settings';
import Profile from './components/Profile';
import Header from './components/Header';
import Help from './components/Help';
import FAQ from './components/FAQ';
import About from './components/About';
import './App.css';

function App() {
  const [details, setDetails] = useState([]);
  const [activeSection, setActiveSection] = useState('overview'); // Initialize active section state

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/')
      .then(res => {
        const data = res.data;
        setDetails(data); // Update details state with fetched data
      })
      .catch(err => {
        console.error('Error fetching data:', err);
      });
  }, []); // Empty dependency array ensures useEffect runs only once on component mount

  return (
    <div className="container">
      <Header />
      <div className="App-body">
        <Sidebar onMenuClick={section => setActiveSection(section)} />
        <main className="main-content">
          {activeSection === 'overview' && <Overview seeds={details} />}
          {activeSection === 'storage' && <Storage />}
          {activeSection === 'reports' && <Reports />}
          {activeSection === 'settings' && <Settings />}
          {activeSection === 'profile' && <Profile />}
          {activeSection === 'help' && <Help />}
          {activeSection === 'faq' && <FAQ />}
          {activeSection === 'about' && <About />}
        </main>
      </div>
    </div>
  );
}

export default App;
