import React, { useState } from 'react';
import './header.css';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = () => {
    // Logic to handle the search and load matching components
    console.log("Searching for:", searchQuery);
    // You can add the logic to load components based on the search query here
  };

  const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  return (
    <header className="header">
      <div className="logo">
        {/* Path to your logo image */}
        <img src="path/to/your/logo.png" alt="Logo" />
        <span>Smart Storage</span>
      </div>
      <div className="search">
        <input 
          type="text" 
          placeholder="Search for data & reports..." 
          value={searchQuery}
          onChange={handleInputChange}
        />
        <button onClick={handleSearch}>
          <i className="fas fa-search"></i>Search
        </button>
      </div>
      <div className="user-profile">
        <div className="notifications">
          <div className="dropdown">
            <i className="fas fa-bell"></i>
            <div className="dropdown-content">
              {/* Content for bell notifications */}
              <p>No new notifications</p>
            </div>
          </div>
          <div className="dropdown">
            <i className="fas fa-envelope"></i>
            <div className="dropdown-content">
              {/* Content for envelope notifications */}
              <p>No new messages</p>
            </div>
          </div>
          <div className="dropdown">
            <i className="fas fa-cog"></i>
            <div className="dropdown-content">
              {/* Content for settings */}
              <p>Settings</p>
            </div>
          </div>
        </div>
        {/* Default user image */}
        <img src="path/to/default/user.jpg" alt="User" />
        <span>John Doe</span>
      </div>
    </header>
  );
}

export default Header;
