import React, { useState } from 'react';
import Overview from './Overview';
import Storage from './Storage';
import Reports from './Reports';
import Settings from './Settings';
import Header from './Header';
import Help from './elp';
import FAQ from './components/FAQ';
import About from './components/About';
import './sidebar.css';

function Sidebar() {
  const [activeSection, setActiveSection] = useState('overview');

  return (
    <div className="container">
      <aside className="sidebar">
        <nav className="menu">
          <ul>
            <li onClick={() => setActiveSection('overview')} className="active" id="dashboardBtn">Dashboard</li>
            <li onClick={() => setActiveSection('storage')} id="storageBtn">Storage</li>
            <li onClick={() => setActiveSection('reports')} id="reportsBtn">Reports</li>
            <li onClick={() => setActiveSection('settings')} id="settingsBtn">Settings</li>
          </ul>
        </nav>
        <nav className="footer-menu">
          <ul>
            <li onClick={() => setActiveSection('help')} id="helpBtn">Help</li>
            <li onClick={() => setActiveSection('faq')} id="faqBtn">FAQ</li>
            <li onClick={() => setActiveSection('about')} id="aboutBtn">About Us</li>
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <Header />
        {activeSection === 'overview' && <Overview />}
        {activeSection === 'storage' && <Storage />}
        {activeSection === 'reports' && <Reports />}
        {activeSection === 'settings' && <Settings />}
        {activeSection === 'help' && <Help />}
        {activeSection === 'faq' && <FAQ />}
        {activeSection === 'about' && <About />}
      </main>
    </div>
  );
}

export default Sidebar;
