import axios from 'axios';
import React, { Component } from 'react';
import Sidebar from './components/Sidebar';
import Overview from './components/Overview';
import Storage from './components/Storage';
import Reports from './components/Reports';
import Settings from './components/Settings';
import Profile from './components/Profile';
import Header from './components/Header';
import Help from './components/Help';
import FAQ from './components/FAQ';
import About from './components/About';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      details: [], // Initialize as an empty array
      activeSection: 'overview' // Initialize active section here
    };
  }

  componentDidMount() {
    axios.get('http://127.0.0.1:8000/')
      .then(res => {
        const data = res.data;
        if (Array.isArray(data)) {
          this.setState({ details: data });
        } else {
          console.error('Error: API response is not an array');
        }
      })
      .catch(err => {
        console.error('Error fetching data:', err);
      });
  }

  render() {
    const { activeSection, details } = this.state;

    return (
      <div className="container">
        <Header />
        <div className="App-body">
          <Sidebar
            activeSection={activeSection}
            onMenuClick={section => this.setState({ activeSection: section })}
          />
          <main className="main-content">
            {activeSection === 'overview' && <Overview seeds={details} />}
            {activeSection === 'storage' && <Storage />}
            {activeSection === 'reports' && <Reports />}
            {activeSection === 'settings' && <Settings />}
            {activeSection === 'profile' && <Profile />}
            {activeSection === 'help' && <Help />}
            {activeSection === 'faq' && <FAQ />}
            {activeSection === 'about' && <About />}
          </main>
        </div>
      </div>
    );
  }
}

export default App;
