import React, { useState } from 'react';
import './profile.css';

function Profile() {
  const [activeTab, setActiveTab] = useState('user'); // State to manage active tab

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <section id="settings" className="section">
      <div className="settings-content">
        {/* Placeholder for settings content */}
      </div>
      <h1>Profile</h1>
      <p>Manage your profile here.</p>

      {/* Tabs Navigation */}
      <div className="tab-navigation">
        <button className={activeTab === 'user' ? 'active' : ''} onClick={() => handleTabClick('user')}>
          User
        </button>
        <button className={activeTab === 'account management' ? 'active' : ''} onClick={() => handleTabClick('account management')}>
          Account Management
        </button>
        <button className={activeTab === 'data' ? 'active' : ''} onClick={() => handleTabClick('data')}>
          All Data
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {activeTab === 'user' && (
          <div>
            <h2>User</h2>
            <div className="cards">
              {/* Example: Display user profile information */}
              <p>Name: John Doe</p>
              <p>Email: john.doe@example.com</p>
              {/* Add more user-related information */}
            </div>
          </div>
        )}

        {activeTab === 'account management' && (
          <div>
            <h2>Account Management</h2>
            {/* Example: Form to change password */}
            <form>
              <label>New Password:</label>
              <input type="password" />
              <button type="submit">Change Password</button>
            </form>
          </div>
        )}

        {activeTab === 'data' && (
          <div>
            <h2>All Data</h2>
            {/* Example: Display all data or manage data settings */}
            <p>View or manage all data here.</p>
          </div>
        )}
      </div>
    </section>
  );
}

export default Profile;
