import React from 'react';
import './sidebar.css';

function Sidebar() {
  const handleMenuClick = (section) => {
    switch (section) {
      case 'overview':
        window.location.href = '/overview'; // Navigate to the overview page
        break;
      case 'storage':
        window.location.href = '/storage'; // Navigate to the storage page
        break;
      case 'reports':
        window.location.href = '/reports'; // Navigate to the reports page
        break;
      case 'settings':
        window.location.href = '/settings'; // Navigate to the settings page
        break;
      default:
        break;
    }
  };

  return (
    <aside className="sidebar">
      <div className="logo">
        <img src="logo.png" alt="Logo" />
        <span>Smart Storage</span>
      </div>
      <nav className="menu">
        <ul>
          <li onClick={() => handleMenuClick('overview')} className="active">Dashboard</li>
          <li onClick={() => handleMenuClick('storage')}>Storage</li>
          <li onClick={() => handleMenuClick('reports')}>Reports</li>
          <li onClick={() => handleMenuClick('settings')}>Settings</li>
        </ul>
      </nav>
    </aside>
  );
}

export default Sidebar;
