import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from './Card';
import './overview.css';

function Overview() {
  const [seeds, setSeeds] = useState([]);
  const [monitoring, setMonitoring] = useState([]);
  const [activeTab, setActiveTab] = useState('overview'); // State to manage active tab

  useEffect(() => {
    const fetchData = async () => {
      try {
        const seedsResponse = await axios.get('http://127.0.0.1:8000/api/seeds/'); // Update URL as per your Django setup
        const monitoringResponse = await axios.get('http://127.0.0.1:8000/api/monitoring/'); // Update URL as per your Django setup

        setSeeds(seedsResponse.data);
        setMonitoring(monitoringResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []); // Empty dependency array to run once on mount

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <section id="overview" className="section">
      <h1>Dashboard</h1>

      {/* Tabs Navigation */}
      <div className="tab-navigation">
        <button className={activeTab === 'overview' ? 'active' : ''} onClick={() => handleTabClick('overview')}>
          Overview
        </button>
        <button className={activeTab === 'security' ? 'active' : ''} onClick={() => handleTabClick('security')}>
          Security
        </button>
        <button className={activeTab === 'monitoring' ? 'active' : ''} onClick={() => handleTabClick('monitoring')}>
          Monitoring
        </button>
        <button className={activeTab === 'recommendations' ? 'active' : ''} onClick={() => handleTabClick('recommendations')}>
          Recommendations
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {activeTab === 'overview' && (
          <div>
            <h2>Overview</h2>
            <div className="cards">
              {Array.isArray(seeds) && seeds.map((seed) => (
                <Card key={seed.id} title={seed.SeedType} value={seed.SeedQuantity} unit="seeds" />
              ))}
            </div>
          </div>
        )}

        {activeTab === 'security' && (
          <div>
            <h2>Security Content</h2>
            {/* Add content for Security */}
          </div>
        )}

        {activeTab === 'monitoring' && (
          <div>
            <h2>Monitoring Content</h2>
            {/* Add content for Monitoring */}
          </div>
        )}

        {activeTab === 'recommendations' && (
          <div>
            <h2>Recommendations Content</h2>
            {/* Add content for Recommendations */}
          </div>
        )}
      </div>
    </section>
  );
}

export default Overview;
