import React from 'react';
import './sidebar.css';

function Sidebar({ onMenuClick }) {

  const [ setActiveSection] = useState('overview');

  const handleMenuClick = (section) => {
    setActiveSection(section);
  };

  return (
    <aside className="sidebar">
      <nav className="menu">
        <ul>
          <li onClick={() => handleMenuClick('overview')} id="dashboardBtn">Dashboard</li>
          <li onClick={() => handleMenuClick('storage')} id="storageBtn">Storage</li>
          <li onClick={() => handleMenuClick('reports')} id="reportsBtn">Reports</li>
          <li onClick={() => handleMenuClick('settings')} id="settingsBtn">Settings</li>
        </ul>
      </nav>
      <nav className="footer-menu">
        <ul>
          <li onClick={() => onMenuClick('help')} id="helpBtn">Help</li>
          <li onClick={() => onMenuClick('faq')} id="faqBtn">FAQ</li>
          <li onClick={() => onMenuClick('about')} id="aboutBtn">About Us</li>
        </ul>
      </nav>
    </aside>
  );
}

export default Sidebar;
